mapper.delete:
delete from invoice 


		and title_content = #{titleContent}


		and taxpayer_identification_code = #{taxpayerIdentificationCode}


		and register_addr = #{registerAddr}


		and register_tel = #{registerTel}


		and deposit_bank = #{depositBank}


		and bank_account = #{bankAccount}


		and business_licence_url = #{businessLicenceUrl}


		and user_id = #{userId}


		and is_default = #{isDefault}


		and create_type = #{createType}


		and update_time = #{updateTime}


		and create_time = #{createTime}


		and platform_id = #{platformId}


		and title_type = #{titleType}


		and is_delete = #{isDelete}
