		and order_code = #{orderCode}


		and return_code = #{returnCode}


		and refundment_code = #{refundmentCode}


		and amount = #{amount}


		and channel = #{channel}


		and apply_time = #{applyTime}


		and refundment_time = #{refundmentTime}


		and voucher = #{voucher}


		and refundment_status = #{refundmentStatus}


		and refundment_type = #{refundmentType}


		and platform_id = #{platformId}


		and update_time = #{updateTime}


		and create_time = #{createTime}
