		and enquiry_code = #{enquiryCode}


		and enquiry_status = #{enquiryStatus}


		and enquiry_audit_status = #{enquiryAuditStatus}


		and affirm_status = #{affirmStatus}


		and product_id = #{productId}


		and product_name = #{productName}


		and product_price = #{productPrice}


		and product_num = #{productNum}


		and user_id = #{userId}


		and user_name = #{userName}


		and user_phone = #{userPhone}


		and c_id = #{cId}


		and c_name = #{cName}


		and ask_time = #{askTime}


		and offer_time = #{offerTime}


		and enquiry_remark_user = #{enquiryRemarkUser}


		and enquiry_remark_c = #{enquiryRemarkC}


		and platform_id = #{platformId}


		and audit_reason = #{auditReason}


		and link_phone = #{linkPhone}


		and cancel_reason_user = #{cancelReasonUser}


		and cancel_reason_c = #{cancelReasonC}


		and order_code = #{orderCode}


		and merchant_id = #{merchantId}


		and merchant_name = #{merchantName}


		and enquiry_remark_c_user = #{enquiryRemarkCUser}


		and create_time = #{createTime}


