mapper.select:
SELECT

FROM jd_order_await_queue


		and so_id = #{po.soId}


		and so_child_id = #{po.soChildId}


		and order_code = #{po.orderCode}


		and random_number = #{po.randomNumber}


		and create_time = #{po.createTime}


		and is_pay_true = #{po.isPayTrue}


		and jd_order_id = #{po.jdOrderId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM jd_order_await_queue


