mapper.select:
SELECT

FROM thirdparty_await_queue


		and so_id = #{po.soId}


		and order_code = #{po.orderCode}


		and random_number = #{po.randomNumber}


		and create_time = #{po.createTime}


		and cash_pay_type = #{po.cashPayType}


		and is_pay_true = #{po.isPayTrue}


		and thirdparty_type = #{po.thirdpartyType}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM thirdparty_await_queue


		and so_id = #{po.soId}


