SELECT av.id, pav.id product_att_valueId, pav.picture_url, av.parent_id, av.attribute_name_id, av.value, av.sort_value, av.specification_code, av.platform_id, av.create_time, av.update_time FROM attribute_value av LEFT JOIN product_att_value pav on pav.att_value_id = av.id LEFT JOIN product_att_name pan on pan.id = pav.product_att_name_id
and pan.product_id = #{productId}
and pan.att_name_id = #{attNameId}

SELECT av.id, av.parent_id, av.attribute_name_id, av.value, av.sort_value, av.specification_code, av.platform_id, av.create_time, av.update_time FROM attribute_value av LEFT JOIN standard_product_unit_att_value spav on spav.att_value_id = av.id LEFT JOIN standard_product_unit_att_name span on span.id = spav.standard_product_unit_att_name_id
and span.standard_product_unit_id = #{productId}
and span.att_name_id = #{attNameId}
