SELECT

	FROM blessing_coin_banner


			and id in(SELECT blessing_coin_banner_id FROM blessing_coin_banner_company WHERE company_id = #{po.companyId})


			and name = #{po.name}


			and type = #{po.type}


			and sort_value = #{po.sortValue}


			and location = #{po.location}


			and standard_unit_id = #{po.standardUnitId}


			and activity_id = #{po.activityId}


			and picture_url = #{po.pictureUrl}


			and path = #{po.path}


			and is_show = #{po.isShow}


			and create_time = #{po.createTime}


			and update_time = #{po.updateTime}


			and platform_id = #{po.platformId}




			ORDER BY ${page.orderBy}


