mapper.select:
SELECT

FROM brand


		and name = #{po.name}


		and chinese_name = #{po.chineseName}


		and english_name = #{po.englishName}


		and alias = #{po.alias}


		and log_url = #{po.logUrl}


		and ownedcompany_chinese_name = #{po.ownedcompanyChineseName}


		and ownedcompany_english_name = #{po.ownedcompanyEnglishName}


		and introduction = #{po.introduction}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM brand


		and name = #{po.name}


