SELECT
csa.id,
csa.name,
csa.leading_end_category_id,
csa.picture_url,
csa.standard_unit_id,
csa.sort_value,
csa.is_show,
csa.create_userid,
csa.create_username,
csa.create_userip,
csa.create_usermac,
csa.create_time,
csa.update_userid,
csa.update_username,
csa.update_userip,
csa.update_usermac,
csa.update_time,
csa.client_versionno,
csa.platform_id,
su.name standard_unit_name,
len.name leading_end_category_name
FROM card_stamps_administration csa
LEFT JOIN standard_unit su on csa.standard_unit_id = su.id
LEFT JOIN leading_end_category len on csa.leading_end_category_id = len.id


		and csa.name = #{po.name}


		and csa.leading_end_category_id = #{po.leadingEndCategoryId}


		and csa.picture_url = #{po.pictureUrl}


		and csa.standard_unit_id = #{po.standardUnitId}


		and csa.sort_value = #{po.sortValue}


		and csa.is_show = #{po.isShow}


		and csa.create_userid = #{po.createUserid}


		and csa.create_username = #{po.createUsername}


		and csa.create_userip = #{po.createUserip}


		and csa.create_usermac = #{po.createUsermac}


		and csa.create_time = #{po.createTime}


		and csa.update_userid = #{po.updateUserid}


		and csa.update_username = #{po.updateUsername}


		and csa.update_userip = #{po.updateUserip}


		and csa.update_usermac = #{po.updateUsermac}


		and csa.update_time = #{po.updateTime}


		and csa.client_versionno = #{po.clientVersionno}


		and csa.platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY csa.sort_value



limit #{page.limitStart},#{page.pageSize}
