mapper.select:
SELECT

FROM card_thirdparty_att_value


		and card_type = #{po.cardType}


		and thirdparty_att_value_id = #{po.thirdpartyAttValueId}


		and remark = #{po.remark}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM card_thirdparty_att_value


		and card_type = #{po.cardType}


		and thirdparty_att_value_id = #{po.thirdpartyAttValueId}


		and remark = #{po.remark}


		and platform_id = #{po.platformId}

SELECT

FROM card_thirdparty_att_value


		and card_type = #{po.cardType}


		and thirdparty_att_value_id = #{po.thirdpartyAttValueId}


		and remark = #{po.remark}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc
