mapper.select:
SELECT

FROM category_att_value


		and category_att_name_id = #{po.categoryAttNameId}


		and att_value_id = #{po.attValueId}


		and att_value_custom = #{po.attValueCustom}


		and sort_value = #{po.sortValue}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM category_att_value


		and category_att_name_id = #{po.categoryAttNameId}


		and att_value_id = #{po.attValueId}


		and att_value_custom = #{po.attValueCustom}


		and sort_value = #{po.sortValue}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}

SELECT

FROM category_att_value


		and category_att_name_id = #{po.categoryAttNameId}


		and att_value_id = #{po.attValueId}


		and att_value_custom = #{po.attValueCustom}


		and sort_value = #{po.sortValue}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


