mapper.select:
SELECT

FROM category_banner


		and banner_name = #{po.bannerName}


		and banner_img = #{po.bannerImg}


		and link_type = #{po.linkType}


		and link_url = #{po.linkUrl}


		and link_param = #{po.linkParam}


		and external_link_id = #{po.externalLinkId}


		and standard_unit_combination_id = #{po.standardUnitCombinationId}


		and standard_unit_id = #{po.standardUnitId}


		and local_param_id = #{po.localParamId}


		and linkable_id = #{po.linkableId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM category_banner


		and banner_name = #{po.bannerName}


		and banner_img = #{po.bannerImg}


		and link_type = #{po.linkType}


		and link_url = #{po.linkUrl}


		and link_param = #{po.linkParam}


