mapper.select:
SELECT

FROM category_tree_node_relation


		and back_tree_node_id = #{po.backTreeNodeId}


		and front_tree_node_id = #{po.frontTreeNodeId}


		and sort_value = #{po.sortValue}


		and platform_id = #{po.platformId}


		and type = #{po.type}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM category_tree_node_relation


