SELECT ctn.id, ctn.category_id, ctn.category_tree_id, ctn.parent_id, ctn.list_sort, c.name, c.description, c.category_lable, c.serial_number FROM category_tree_node ctn inner join category c on c.id = ctn.category_id
and ctn.category_tree_id = #{po.categoryTreeId}
and ctn.platform_id = #{po.platformId}
order by parent_id, list_sort

SELECT ctn.id, ctn.category_id, ctn.category_tree_id, ctn.parent_id, ctn.list_sort, c.name, c.description, c.category_lable
FROM category_tree_node ctn inner join category c on c.id = ctn.category_id
and parent_id= #{po.parentId}
and c.name = #{po.name}
and c.platform_id = #{po.platformId}
and category_tree_id = #{po.categoryTreeId}
order by parent_id, list_sort

SELECT ctn.id, ctn.category_id, ctn.category_tree_id, ctn.parent_id, ctn.list_sort, c.name, c.description, c.category_lable FROM category_tree_node ctn inner join category c on c.id = ctn.category_id
and parent_id= #{po.parentId}
and c.serial_number = #{po.serialNumber}
and c.platform_id = #{po.platformId}
and category_tree_id = #{po.categoryTreeId}
order by parent_id, list_sort

SELECT ctn.id, ctn.parent_id, ctn.pids, ctn.category_tree_id, ctn.category_id, ctn.list_sort, ctn.platform_id, ctn.create_time, ctn.update_time, cb.banner_img, cb.link_type, cb.link_url, cb.link_param, cb.external_link_id, cb.standard_unit_combination_id, cb.standard_unit_id, cb.local_param_id, c.`name`, c.description, cb.linkable_id FROM category_tree_node ctn,category_banner cb,category c
and ctn.category_id = c.id and c.category_banner_id = cb.id
and ctn.parent_id = #{po.parentId}
and ctn.pids = #{po.pids}
and ctn.category_tree_id = #{po.categoryTreeId}
and ctn.category_id = #{po.categoryId}
and ctn.list_sort = #{po.listSort}
and ctn.platform_id = #{po.platformId}
and ctn.create_time = #{po.createTime}
and ctn.update_time = #{po.updateTime}
ORDER BY ${page.orderBy}
ORDER BY ctn.list_sort,ctn.create_time

select 
c.serial_number 
from category c,category_tree_node ct 
where c.id = ct.category_id 
AND ct.category_tree_id = #{categoryTreeId} AND ct.parent_id=#{parentId} 
ORDER BY CAST(c.serial_number AS signed) desc limit 0,1;
