mapper.select:
SELECT

FROM category_tree


		and type = #{po.type}


		and name like concat('%',#{po.name},'%')


		and company_id = #{po.companyId}


		and enterprise_id = #{po.enterpriseId}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and series_type = #{po.seriesType}


		and start_using = #{po.startUsing}


		and company_type = #{po.companyType}


		and web_start = #{po.webStart}


		and is_default = #{po.isDefault}




		ORDER BY ${page.orderBy}


		ORDER BY start_using desc,create_time



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM category_tree


		and type = #{po.type}


		and name like concat('%',#{po.name},'%')


		and company_id = #{po.companyId}


		and enterprise_id = #{po.enterpriseId}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and series_type = #{po.seriesType}


		and start_using = #{po.startUsing}


		and company_type = #{po.companyType}


		and web_start = #{po.webStart}


		and is_default = #{po.isDefault}

SELECT

FROM category_tree


		and type = #{po.type}


		and name = #{po.name}


		and company_id = #{po.companyId}


		and enterprise_id = #{po.enterpriseId}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and series_type = #{po.seriesType}


		and start_using = #{po.startUsing}


		and company_type = #{po.companyType}


		and web_start = #{po.webStart}


		and is_default = #{po.isDefault}




		ORDER BY ${page.orderBy}


		ORDER BY id desc
