mapper.select:
SELECT

FROM freight_template


		and name like concat('%',#{po.name},'%')


		and merchant_id = #{po.merchantId}


		and shipments_explain = #{po.shipmentsExplain}


		and is_exemption = #{po.isExemption}


		and is_valid = #{po.isValid}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and store_id = #{po.storeId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM freight_template


		and name like concat('%',#{po.name},'%')


		and merchant_id = #{po.merchantId}


		and shipments_explain = #{po.shipmentsExplain}


		and is_exemption = #{po.isExemption}


		and is_valid = #{po.isValid}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and store_id = #{po.storeId}

SELECT

FROM freight_template


		and name = #{po.name}


		and merchant_id = #{po.merchantId}


		and shipments_explain = #{po.shipmentsExplain}


		and is_exemption = #{po.isExemption}


		and is_valid = #{po.isValid}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and store_id = #{po.storeId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc
