SELECT

FROM jd_price_config


		and standard_company_rate = #{po.standardCompanyRate}


		and demoCompanys_company_rate = #{po.democompanysCompanyRate}


		and competing_company_rate = #{po.competingCompanyRate}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

