SELECT

FROM jd_product_inner_id


		and inner_product_id = #{po.innerProductId}


		and inner_sku_id = #{po.innerSkuId}


		and inner_su_id = #{po.innerSuId}


		and inner_product_unit_id = #{po.innerProductUnitId}


		and inner_pu_id = #{po.innerPuId}


		and inner_picture_id = #{po.innerPictureId}


		and inner_att_value_id = #{po.innerAttValueId}




		ORDER BY ${page.orderBy}


		ORDER BY jd_sku_id desc		



limit #{page.limitStart},#{page.pageSize}

