SELECT

FROM membership


		and membership_code = #{po.membershipCode}


		and membership_name = #{po.membershipName}


		and membership_log_img_url = #{po.membershipLogImgUrl}


		and linked_sku_id = #{po.linkedSkuId}


		and category_id = #{po.categoryId}


		and category_name = #{po.categoryName}


		and valid_period_val = #{po.validPeriodVal}


		and valid_period_unit = #{po.validPeriodUnit}


		and remarks = #{po.remarks}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

