mapper.select:
SELECT

FROM merchant_prod_picture


		and merchant_picture_id = #{po.merchantPictureId}


		and type = #{po.type}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and merchant_prod_id = #{po.merchantProdId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM merchant_prod_picture


		and merchant_picture_id = #{po.merchantPictureId}


		and type = #{po.type}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and merchant_prod_id = #{po.merchantProdId}

SELECT

FROM merchant_prod_picture


		and merchant_picture_id = #{po.merchantPictureId}


		and type = #{po.type}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and merchant_prod_id = #{po.merchantProdId}




		ORDER BY ${page.orderBy}


		ORDER BY merchant_picture_id
