mapper.select:
SELECT

FROM merchant_prod_sales_record


		and sales_volume = #{po.salesVolume}


		and sales_date = #{po.salesDate}


		and standard_product_unit_id = #{po.standardProductUnitId}


		and standard_unit_id = #{po.standardUnitId}


		and merchant_id = #{po.merchantId}


		and chinese_name = #{po.chineseName}


		and english_name = #{po.englishName}


		and commodity_product_unit_id = #{po.commodityProductUnitId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

