mapper.select:
SELECT

FROM merchant_prod_video


		and merchant_prod_id = #{po.merchantProdId}


		and merchant_video_id = #{po.merchantVideoId}


		and name = #{po.name}


		and type = #{po.type}


		and thumbnail_url = #{po.thumbnailUrl}


		and video_url = #{po.videoUrl}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM merchant_prod_video


		and merchant_prod_id = #{po.merchantProdId}


		and merchant_video_id = #{po.merchantVideoId}


		and name = #{po.name}


		and type = #{po.type}


