mapper.select:
SELECT

FROM merchant_product_articles_tax


		and tax_number = #{po.taxNumber}


		and merchant_explain = #{po.merchantExplain}


		and merchant_range = #{po.merchantRange}


		and tax_rale = #{po.taxRale}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

