mapper.select:
SELECT

FROM merchant_product_feedback


		and merchant_product_id = #{po.merchantProductId}


		and merchant_product_serial_number = #{po.merchantProductSerialNumber}


		and merchant_product_name = #{po.merchantProductName}


		and attribute_name = #{po.attributeName}


		and sale_price = #{po.salePrice}


		and sell_platform_id = #{po.sellPlatformId}


		and user_name = #{po.userName}


		and account = #{po.account}


		and phone = #{po.phone}


		and content = #{po.content}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and feedback_status = #{po.feedbackStatus}


		and receiver_id = #{po.receiverId}


		and accept_result = #{po.acceptResult}


		and certificate_url = #{po.certificateUrl}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

