mapper.select:
SELECT

FROM merchant_product_promotion


		and merchant_product_id = #{po.merchantProductId}


		and product_id = #{po.productId}


		and sku_id = #{po.skuId}


		and merchant_id = #{po.merchantId}


		and promotion_id = #{po.promotionId}


		and promotion_type = #{po.promotionType}


		and promotion_price = #{po.promotionPrice}


		and promotion_start_time = #{po.promotionStartTime}


		and promotion_end_time = #{po.promotionEndTime}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

