mapper.select:
SELECT

FROM merchant


		and name LIKE concat('%',#{po.name},'%')


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and operate_user_id = #{po.operateUserId}


		and operate_user_name = #{po.operateUserName}


		and is_stop= #{po.isStop}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM merchant


		and name LIKE concat('%',#{po.name},'%')


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and operate_user_id = #{po.operateUserId}


		and operate_user_name = #{po.operateUserName}


		and is_stop= #{po.isStop}

SELECT

FROM merchant


		and name like '%${po.name}%'


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and operate_user_id = #{po.operateUserId}


		and operate_user_name = #{po.operateUserName}


		and is_stop= #{po.isStop}




		ORDER BY ${page.orderBy}


		ORDER BY id ASC
