mapper.select:
SELECT

FROM sell_platform_merchant_prod


		and sell_platform_id = #{po.sellPlatformId}


		and merchant_product_id = #{po.merchantProductId}


		and sale_price = #{po.salePrice}


		and path = #{po.path}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM sell_platform_merchant_prod


		and sell_platform_id = #{po.sellPlatformId}


		and merchant_product_id = #{po.merchantProductId}


		and sale_price = #{po.salePrice}


		and path = #{po.path}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}

SELECT

FROM sell_platform_merchant_prod


		and sell_platform_id = #{po.sellPlatformId}


		and merchant_product_id = #{po.merchantProductId}


		and sale_price = #{po.salePrice}


		and path = #{po.path}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc

SELECT
spmp.id,
spmp.sell_platform_id,
spmp.merchant_product_id,
spmp.sale_price,
spmp.path,
spmp.create_time,
spmp.update_time,
spmp.platform_id,
sp.`name`
FROM sell_platform_merchant_prod spmp
LEFT JOIN sell_platform sp on sp.id = spmp.sell_platform_id


		and spmp.sell_platform_id = #{po.sellPlatformId}


		and spmp.merchant_product_id = #{po.merchantProductId}


		and spmp.sale_price = #{po.salePrice}


		and spmp.path = #{po.path}


		and spmp.create_time = #{po.createTime}


		and spmp.update_time = #{po.updateTime}


		and spmp.platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY spmp.id
