mapper.select:
SELECT

FROM sell_platform


		and name = #{po.name}


		and picture_url = #{po.pictureUrl}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and status = #{po.status}


		and sort_value = #{po.sortValue}


		and remark = #{po.remark}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM sell_platform


		and name = #{po.name}


		and picture_url = #{po.pictureUrl}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and status = #{po.status}


		and sort_value = #{po.sortValue}


		and remark = #{po.remark}

SELECT

FROM sell_platform


		and name = #{po.name}


		and picture_url = #{po.pictureUrl}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and status = #{po.status}


		and sort_value = #{po.sortValue}


		and remark = #{po.remark}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value DESC
