mapper.select:
select att_value_id from standard_product_unit_att_value
where
standard_product_unit_att_name_id=(
select id from
standard_product_unit_att_name
where standard_product_unit_id = #{standardProductUnitId}
and
type=1
and att_name_id=#{attNameId}
)

SELECT

FROM standard_product_unit_att_value


		and standard_product_unit_att_name_id =
		#{po.standardProductUnitAttNameId}


		and att_value_id = #{po.attValueId}


		and specification_code = #{po.specificationCode}


		and att_value_custom = #{po.attValueCustom}


		and sort_value = #{po.sortValue}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



	limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM standard_product_unit_att_value


		and standard_product_unit_att_name_id =
		#{po.standardProductUnitAttNameId}


		and att_value_id = #{po.attValueId}


		and specification_code = #{po.specificationCode}


		and att_value_custom = #{po.attValueCustom}


		and sort_value = #{po.sortValue}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}

SELECT

FROM standard_product_unit_att_value


		and standard_product_unit_att_name_id =
		#{po.standardProductUnitAttNameId}


		and att_value_id = #{po.attValueId}


		and specification_code = #{po.specificationCode}


		and att_value_custom = #{po.attValueCustom}


		and sort_value = #{po.sortValue}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value

SELECT spuav.att_value_custom from standard_product_unit_att_value spuav 
LEFT JOIN standard_product_unit_att_name spuan on spuav.standard_product_unit_att_name_id = spuan.id 
WHERE spuan.att_name_id = 5 and spuan.standard_product_unit_id = #{standardProductUnitId}
