mapper.select:
SELECT

FROM standard_product_unit_picture_record


		and standard_product_unit_record_id = #{po.standardProductUnitRecordId}


		and type = #{po.type}


		and picture_id = #{po.pictureId}


		and sort_value = #{po.sortValue}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



limit #{page.limitStart},#{page.pageSize}

