SELECT

FROM standard_product_unit_record


		and standard_product_unit_id = #{po.standardProductUnitId}


		and product_serial_number = #{po.productSerialNumber}


		and brand_id = #{po.brandId}


		and category_tree_node_id = #{po.categoryTreeNodeId}


		and title = #{po.title}


		and name = #{po.name}


		and chinese_name = #{po.chineseName}


		and market_price = #{po.marketPrice}


		and tax_no = #{po.taxNo}


		and ean_no = #{po.eanNo}


		and place_of_origin = #{po.placeOfOrigin}


		and calculation_unit = #{po.calculationUnit}


		and status = #{po.status}


		and is_available = #{po.isAvailable}


		and product_details = #{po.productDetails}


		and create_userid = #{po.createUserid}


		and create_username = #{po.createUsername}


		and create_userip = #{po.createUserip}


		and create_usermac = #{po.createUsermac}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

