mapper.select:
SELECT

FROM standard_unit_combination_category


		and standard_unit_combination_id =
		#{po.standardUnitCombinationId}


		and category_tree_node_id = #{po.categoryTreeNodeId}


		and sort_value = #{po.sortValue}


		and type = #{po.type}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



	limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM standard_unit_combination_category


		and standard_unit_combination_id =
		#{po.standardUnitCombinationId}


		and category_tree_node_id = #{po.categoryTreeNodeId}


		and sort_value = #{po.sortValue}


		and type = #{po.type}

SELECT

FROM standard_unit_combination_category


		and standard_unit_combination_id =
		#{po.standardUnitCombinationId}


		and category_tree_node_id = #{po.categoryTreeNodeId}


		and sort_value = #{po.sortValue}


		and type = #{po.type}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value
