mapper.select:
SELECT

FROM standard_unit_combination_su


		and standard_unit_combination_id = #{po.standardUnitCombinationId}


		and standard_unit_id = #{po.standardUnitId}


		and sort_value = #{po.sortValue}


		and source = #{po.source}


		and sell_state = #{po.sellState}


		and third_sku_id = #{po.thirdSkuId}


		and third_sku_name like concat('%',#{po.thirdSkuName},'%')





		ORDER BY ${page.orderBy}


		ORDER BY sort_value



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM standard_unit_combination_su


		and standard_unit_combination_id = #{po.standardUnitCombinationId}


		and standard_unit_id = #{po.standardUnitId}


		and sort_value = #{po.sortValue}


		and source = #{po.source}


		and sell_state = #{po.sellState}


		and third_sku_id = #{po.thirdSkuId}


		and third_sku_name like concat('%',#{po.thirdSkuName},'%')

SELECT

FROM standard_unit_combination_su


		and standard_unit_combination_id = #{po.standardUnitCombinationId}


		and standard_unit_id = #{po.standardUnitId}


		and sort_value = #{po.sortValue}


		and source = #{po.source}


		and sell_state = #{po.sellState}


		and third_sku_id = #{po.thirdSkuId}


		and standard_unit_combination_id in

			#{standardUnitCombinationId}





		ORDER BY ${page.orderBy}


		ORDER BY sort_value

SELECT sucs.id as id, sucs.standard_unit_combination_id as standard_unit_combination_id, sucs.standard_unit_id as standard_unit_id, sucs.sort_value as sort_value, sucs.source as source, sucs.snapshot as snapshot, sucs.sell_state as sell_state, sucs.check_time as check_time, sucs.third_sku_id as third_sku_id, su.name as standard_unit_name, su.sale_price as sale_price, su.merchant_product_serial_number as merchant_product_serial_number, su.supplier_id as supplier_id, su.is_vendible as is_vendible, su.is_visible as is_visible FROM standard_unit_combination_su sucs left join standard_unit su on sucs.standard_unit_id=su.id WHERE sucs.standard_unit_combination_id = #{standardUnitCombinationId}
ORDER BY ${page.orderBy}
ORDER BY sucs.sort_value
limit #{page.limitStart},#{page.pageSize}

SELECT

FROM standard_unit_combination_su


		and source = #{source}


		and (#{endCheckTime}>=check_time or check_time is null)


ORDER BY check_time,id
limit #{size}
