SELECT su.id, su.merchant_product_serial_number, su.merchant_id, su.standard_product_unit_id, su.name, su.sale_price,su.demo_sale_price,su.competing_sale_price, su.market_price, su.status, su.sold_base, su.store_id, su.stock_warning, su.sale_way, su.buy_type, su.front_serial_number, spu.commodity_template_id, sus.picture_url, sales_volume FROM standard_unit su,standard_product_unit spu, (SELECT su.id,susr.picture_url, (SELECT sum(sales_volume) from merchant_prod_sales_record WHERE standard_unit_id = su.id) sales_volume FROM standard_unit su LEFT JOIN su_serach_rule susr on su.id = susr.standard_unit_id LEFT JOIN merchant m on su.merchant_id = m.id
m.is_stop = 0
and su.is_vendible = 0 and su.is_visible = 0
and su.sale_way in (4,6)
and susr.client_id like concat('%',#{clientId},'/%')
and (susr.company_id like concat('%','-1/','%') or susr.company_id like concat('%',#{companyId},'/%'))
and susr.company_type like concat('%',#{companyType},'/%')
and su.sale_price < #{salePrice}
and (susr.standard_unit_name like CONCAT('%',#{name},'%') or susr.standard_unit_keyword like CONCAT('%',#{name},'%') or su.name like CONCAT('%',#{name},'%') or susr.standard_unit_tag like CONCAT('%',#{name},'%') or susr.standard_unit_category like CONCAT('%',#{name},'%'))
and su.platform_id = #{platformId}
and su.enterprise_id = #{enterpriseId}
and su.buy_type = #{buyType}
and su.front_serial_number = #{frontSerialNumber}
GROUP BY su.id
ORDER BY ${page.orderBy}
ORDER BY REPLACE(susr.standard_unit_keyword,#{name},''),REPLACE(susr.standard_unit_name,#{name},''),REPLACE(susr.standard_unit_tag,#{name},''),REPLACE(susr.standard_unit_category,#{name},'')
limit #{page.limitStart},#{page.pageSize} ) sus WHERE sus.id = su.id and su.standard_product_unit_id = spu.id

