mapper.select:
SELECT

FROM store_menu_node


		and parent_id = #{po.parentId}


		and store_menu_tree_id = #{po.storeMenuTreeId}


		and name = #{po.name}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and sort_value = #{po.sortValue}


		and description = #{po.description}


		and is_all = #{po.isAll}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM store_menu_node


		and parent_id = #{po.parentId}


		and store_menu_tree_id = #{po.storeMenuTreeId}


		and name = #{po.name}


		and create_time = #{po.createTime}


