mapper.select:
SELECT

FROM su_serach_rule


		and standard_unit_id = #{po.standardUnitId}


		and standard_unit_name = #{po.standardUnitName}


		and standard_unit_keyword = #{po.standardUnitKeyword}


		and standard_unit_tag = #{po.standardUnitTag}


		and standard_unit_category = #{po.standardUnitCategory}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM su_serach_rule


		and standard_unit_id = #{po.standardUnitId}


		and standard_unit_name = #{po.standardUnitName}


		and standard_unit_keyword = #{po.standardUnitKeyword}


		and standard_unit_tag = #{po.standardUnitTag}


		and standard_unit_category = #{po.standardUnitCategory}

SELECT

FROM su_serach_rule


		and standard_unit_id = #{po.standardUnitId}


		and standard_unit_name = #{po.standardUnitName}


		and standard_unit_keyword = #{po.standardUnitKeyword}


		and standard_unit_tag = #{po.standardUnitTag}


		and standard_unit_category = #{po.standardUnitCategory}




		ORDER BY ${page.orderBy}


		ORDER BY id desc
