mapper.insert:
insert into merchant_product_store (
store_id,
merchant_product_id,
platform_id
)values

	(#{po.storeId},
	#{po.merchantProductId},
	#{po.platformId}

	)

mapper.delete:
delete from merchant_product_store 


		and store_id = #{storeId}


		and merchant_product_id = #{merchantProductId}


		and platform_id = #{platformId}
