mapper.insert:
insert into picture(
id,
url
)values

	(#{po.id},
	#{po.url}
	)

on duplicate key update
url=VALUES (url)

mapper.delete:
delete from picture 


		and name = #{name}


		and url = #{url}


		and platform_id = #{platformId}


		and sort_value = #{sortValue}


		and type = #{type}


		and create_time = #{createTime}


		and update_time = #{updateTime}
