mapper.insert:
insert into product_att_value(
product_att_name_id,
att_value_id,
platform_id
)values

	(#{po.productAttNameId},
	#{po.attValueId},
	#{po.platformId}
	)

mapper.delete:
delete from product_att_value 


		and product_att_name_id = #{productAttNameId}


		and att_value_id = #{attValueId}


		and att_value_custom = #{attValueCustom}


		and sort_value = #{sortValue}


		and picture_url = #{pictureUrl}


		and create_time = #{createTime}


		and update_time = #{updateTime}


		and platform_id = #{platformId}
