mapper.update:
UPDATE standard_unit_combination_category 


			standard_unit_combination_id = #{standardUnitCombinationId},


			category_tree_node_id = #{categoryTreeNodeId},


			sort_value = #{sortValue},


			type = #{type}


		WHERE id = #{id}

mapper.delete:
delete from standard_unit_combination_category 


		and standard_unit_combination_id = #{standardUnitCombinationId}


		and category_tree_node_id = #{categoryTreeNodeId}


		and sort_value = #{sortValue}


		and type = #{type}
