mapper.insert:
insert into standard_unit_store(
store_id,
standard_unit_id,
platform_id
)values

	(
	#{po.storeId},
	#{po.standardUnitId},
	#{po.platformId}
	)

mapper.delete:
delete from standard_unit_store 


		and store_id = #{storeId}


		and standard_unit_id = #{standardUnitId}


		and platform_id = #{platformId}
