mapper.select:
SELECT

FROM banner


		and name = #{po.name}


		and type = #{po.type}


		and sort_value = #{po.sortValue}


		and merchant_prod_id = #{po.merchantProdId}


		and activity_id = #{po.activityId}


		and picture_url = #{po.pictureUrl}


		and path = #{po.path}


		and start_time = #{po.startTime}


		and finish_time = #{po.finishTime}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY sort_value



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM banner


		and name = #{po.name}


		and type = #{po.type}


		and sort_value = #{po.sortValue}


		and merchant_prod_id = #{po.merchantProdId}


		and activity_id = #{po.activityId}


		and picture_url = #{po.pictureUrl}


		and path = #{po.path}


		and start_time = #{po.startTime}


		and finish_time = #{po.finishTime}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}

SELECT

FROM banner


		and name = #{po.name}


		and type = #{po.type}


		and sort_value = #{po.sortValue}


		and merchant_prod_id = #{po.merchantProdId}


		and activity_id = #{po.activityId}


		and picture_url = #{po.pictureUrl}


		and path = #{po.path}


		and start_time = #{po.startTime}


		and finish_time = #{po.finishTime}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}


		and finish_time > #{po.time}


		and #{po.time} > start_time




		ORDER BY ${page.orderBy}


		ORDER BY sort_value
