mapper.select:
SELECT

FROM coupon_group


		and group_name = #{po.groupName}


		and group_descp = #{po.groupDescp}


		and update_user = #{po.updateUser}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM coupon_group


		and group_name = #{po.groupName}


		and group_descp = #{po.groupDescp}


		and update_user = #{po.updateUser}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}

mapper.sql:
FROM coupon_group


			and group_name like concat('%',#{po.groupName},'%')


			and group_descp = #{po.groupDescp}


			and update_user = #{po.updateUser}


			and create_time = #{po.createTime}


			and update_time = #{po.updateTime}


			and platform_id = #{po.platformId}

