SELECT

FROM er_card_record


		and import_records_id = #{po.importRecordsId}


		and batch = #{po.batch}


		and sku_id = #{po.skuId}


		and sku_name = #{po.skuName}


		and sku_serial_number = #{po.skuSerialNumber}


		and type = #{po.type}


		and card_number = #{po.cardNumber}


		and card_thick = #{po.cardThick}


		and uuid = #{po.uuid}


		and start_time = #{po.startTime}


		and end_time = #{po.endTime}


		and source = #{po.source}


		and face_value = #{po.faceValue}


		and remark = #{po.remark}


		and create_userid = #{po.createUserid}


		and create_username = #{po.createUsername}


		and create_userip = #{po.createUserip}


		and create_usermac = #{po.createUsermac}


		and create_time = #{po.createTime}


		and update_userid = #{po.updateUserid}


		and update_username = #{po.updateUsername}


		and update_userip = #{po.updateUserip}


		and update_usermac = #{po.updateUsermac}


		and client_versionno = #{po.clientVersionno}


		and is_valid = #{po.isValid}


		and order_code = #{po.orderCode}


		and user_login_name = #{po.userLoginName}


		and is_allocation = #{po.isAllocation}


		and allocation_time = #{po.allocationTime}


		and update_time = #{po.updateTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

