SELECT

FROM exchange_batch


		and exchange_id = #{po.exchangeId}


		and batch_id = #{po.batchId}


		and type = #{po.type}


		and add_price = #{po.addPrice}


		and sort = #{po.sort}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

