mapper.select:
SELECT

FROM points_history


		and order_code = #{po.orderCode}


		and user_id = #{po.userId}


		and points = #{po.points}


		and operation_type = #{po.operationType}


		and operation_phase = #{po.operationPhase}


		and type = #{po.type}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM points_history


		and order_code = #{po.orderCode}


		and user_id = #{po.userId}


		and points = #{po.points}


		and operation_type = #{po.operationType}


		and operation_phase = #{po.operationPhase}


		and type = #{po.type}


		and create_time = #{po.createTime}


		and platform_id = #{po.platformId}

