mapper.select:
SELECT

FROM promotion_config


		and promotion_id = #{po.promotionId}


		and rule_time_config_1 = #{po.ruleTimeConfig1}


		and rule_time_config_2 = #{po.ruleTimeConfig2}


		and rule_type = #{po.ruleType}


		and rule_amount = #{po.ruleAmount}


		and rule_amount_ext1 = #{po.ruleAmountExt1}


		and rule_val = #{po.ruleVal}


		and rule_val_1 = #{po.ruleVal1}


		and rule_val_str1 = #{po.ruleValStr1}


		and rule_val_str2 = #{po.ruleValStr2}


		and platform_id = #{po.platformId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

