mapper.select:
SELECT

FROM promotion_gift_item


		and promotion_id = #{po.promotionId}


		and gift_ref = #{po.giftRef}


		and gift_type = #{po.giftType}


		and gift_level = #{po.giftLevel}


		and level_ref = #{po.levelRef}


		and upgrade_amount = #{po.upgradeAmount}


		and extend_ref = #{po.extendRef}


		and platform_id = #{po.platformId}


		and merchant_id = #{po.merchantId}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

