mapper.select:
SELECT

FROM promotion_limit_rule


		and promotion_id = #{po.promotionId}


		and limit_type = #{po.limitType}


		and limit_ref = #{po.limitRef}


		and total_limit = #{po.totalLimit}


		and individual_limit = #{po.individualLimit}


		and status = #{po.status}


		and platform_id = #{po.platformId}


		and rule_type = #{po.ruleType}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

