mapper.select:
SELECT

FROM promotion_scope_record


		and promotion_id = #{po.promotionId}


		and scope_group_id = #{po.scopeGroupId}


		and scope_type = #{po.scopeType}


		and prom_price = #{po.promPrice}


		and is_mutex = #{po.isMutex}


		and status = #{po.status}


		and create_time = #{po.createTime}


		and update_time = #{po.updateTime}


		and mp_id = #{po.mpId}


		and platform_id = #{po.platformId}


		and sku_id = #{po.skuId}




		ORDER BY ${page.orderBy}


		ORDER BY id desc		



limit #{page.limitStart},#{page.pageSize}

SELECT
count(1) cnt
FROM promotion_scope_record


		and promotion_id = #{po.promotionId}


